
var assert = require("assert");
var path = require("path");

var fixtures = path.resolve("test/fixtures");
var files = {
  'a.scss': path.join(fixtures, 'a.sass'),
  'b.scss': path.join(fixtures, 'b.sass'),
  '_c.scss': path.join(fixtures, '_c.sass'),
  'd.scss': path.join(fixtures, 'd.sass'),
  '_e.scss': path.join(fixtures, 'components/_e.sass'),
  'f.scss': path.join(fixtures, 'f.sass'),
  'g.scss': path.join(fixtures, 'g.sass'),
  '_h.scss': path.join(fixtures, 'nested/_h.sass'),
  '_i.scss': path.join(fixtures, 'nested/_i.sass'),
  'i.scss': path.join(fixtures, '_i.sass'),
  'j.scss': path.join(fixtures, 'j.sass'),
  'k.l.scss': path.join(fixtures, 'components/k.l.sass'),
  'm.scss': path.join(fixtures, 'm.sass'),
  '_n.scss': path.join(fixtures, 'compass/_n.sass'),
  '_compass.scss': path.join(fixtures, 'components/_compass.sass')
}

describe('sass-graph', function(){
  var sassGraph = require('../sass-graph');

  describe('parsing a graph of all sass files', function(){
    var graph = sassGraph.parseDir(fixtures, {loadPaths: [fixtures + '/components']});

    it('should have all files', function(){
      assert.equal(Object.keys(files).length, Object.keys(graph.index).length);
    })

    it('should have the correct imports for a.sass', function() {
      assert.deepEqual([files['b.sass']], graph.index[files['a.sass']].imports);
    });

    it('should have the correct importedBy for _c.sass', function() {
      assert.deepEqual([files['b.sass']], graph.index[files['_c.sass']].importedBy);
    });

    it('should have the correct (nested) imports for g.sass', function() {
      var expectedDescendents = [files['_h.sass'], files['_i.sass']];
      var descendents = [];

      graph.visitDescendents(files['g.sass'], function (imp) {
        descendents.push(imp);
        assert.notEqual(expectedDescendents.indexOf(imp), -1);
      });
    });

    it('should ignore custom imports for m.sass', function() {
      assert.deepEqual([files['_compass.sass'] , files['_n.sass']], graph.index[files['m.sass']].imports);
    });

    it('should traverse ancestors of _c.sass', function() {
      ancestors = [];
      graph.visitAncestors(files['_c.sass'], function(k) {
        ancestors.push(k);
      })
      assert.deepEqual([files['b.sass'], files['a.sass']], ancestors);
    });

    it('should prioritize cwd', function() {
      var expectedDescendents = [files['_i.sass']];
      var descendents = [];

      graph.visitDescendents(files['_h.sass'], function (imp) {
        descendents.push(imp);
        assert.notEqual(expectedDescendents.indexOf(imp), -1);
      });
    });
  })

  describe('parseFile', function () {
    it('should parse imports', function () {
      var graph = sassGraph.parseFile(files['a.sass']);
      var expectedDescendents = [files['b.sass'], files['_c.sass']];
      var descendents = [];
      graph.visitDescendents(files['a.sass'], function (imp) {
        descendents.push(imp);
        assert.notEqual(expectedDescendents.indexOf(imp), -1);
      });
      assert.equal(expectedDescendents.length, descendents.length);
    });
  });

  describe('parseFile', function () {
    it('should parse imports with loadPaths', function () {
      var graph = sassGraph.parseFile(files['d.sass'], {loadPaths: [fixtures + '/components']} );
      var expectedDescendents = [files['_e.sass']];
      var descendents = [];
      graph.visitDescendents(files['d.sass'], function (imp) {
        descendents.push(imp);
        assert.notEqual(expectedDescendents.indexOf(imp), -1);
      });
      assert.equal(expectedDescendents.length, descendents.length);
    });
  });

  describe('parseFile', function () {
    it('should thow an error', function () {
      try {
        var graph = sassGraph.parseFile(files['d.sass']);
      } catch (e) {
        assert.equal(e, "File to import not found or unreadable: e");
      }
    });
  });

  describe('parseFile', function () {
    it('should not throw an error for a file with no dependencies with Array having added functions', function () {
      try {
        Array.prototype.foo = function() {
          return false;
        }
        var graph = sassGraph.parseFile(files['f.sass']);
      } catch (e) {
        assert.fail("Error: " + e);
      }
    });
  });
});
